mapper.select:
select count(*) as totCnt
from statistc.requestinfo ri
where left(ri.createDate, 2) = #{year};

SELECT COUNT(*) AS monthlyActiveUsers
FROM statistc.requestInfo ri
WHERE LEFT(ri.createDate, 6) = #{yearMonth};

SELECT COUNT(*) AS dailyActiveUsers
FROM statistc.requestInfo ri
WHERE LEFT(ri.createDate, 8) = #{daily};

SELECT AVG(totalLogins) AS averageTotalLogins
FROM (
    SELECT COUNT(*) AS totalLogins
    FROM statistc.requestInfo
) AS subquery;

SELECT COUNT(*) AS loginsExcludingHolidays
FROM statistc.requestInfo ri
WHERE LEFT(ri.createDate, 6) NOT IN (SELECT SUBSTRING(holiday_date, 3, 6) FROM holidays);
